async function insertImage() {
  // Inserts an image anchored to the last paragraph.
  await Word.run(async (context) => {
    context.document.body.paragraphs
      .getLast()
      .insertParagraph("", "After")
      .insertInlinePictureFromBase64(base64Image, "End");

    await context.sync();
  });
}

async function getImage() {
  // Gets the first image in the document.
  await Word.run(async (context) => {
    const firstPicture = context.document.body.inlinePictures.getFirst();
    firstPicture.load("width, height");

    await context.sync();
    console.log(`Image dimensions: ${firstPicture.width} x ${firstPicture.height}`);
    // Get the image encoded as Base64.
    const base64 = firstPicture.getBase64ImageSrc();

    await context.sync();
    console.log(base64.value);
  });
}

async function setup() {
  // Sets up by adding a dummy paragraph.
  await Word.run(async (context) => {
    const body = context.document.body;
    body.clear();
    body.insertParagraph(
      "Video provides a powerful way to help you prove your point. When you click Online Video, you can paste in the embed code for the video you want to add. You can also type a keyword to search online for the video that best fits your document. ",
      "End"
    );
    body.paragraphs
      .getLast()
      .insertText(
        "To make your document look professionally produced, Word provides header, footer, cover page, and text box designs that complement each other. For example, you can add a matching Online cover page, header, and sidebar. Click Insert and then choose the elements you want from the different Online galleries.",
        "End"
      );
  });
}

// Default helper for invoking an action and handling errors.
async function tryCatch(callback) {
  try {
    await callback();
  } catch (error) {
    // Note: In a production add-in, you'd want to notify the user through your add-in's UI.
    console.error(error);
  }
}

